  `news_word` varchar(50) COLLATE utf8mb4_german2_ci NOT NULL,
  `user_id` bigint(20) NOT NULL,
  PRIMARY KEY (`news_id`)
) ENGINE=MyISAM AUTO_INCREMENT=167 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_german2_ci;

